    private void buttonCadastroActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_buttonCadastroActionPerformed
        // TODO add your handling code here:
        String nivelSelecionado = (String) comboNivel.getSelectedItem();
        if (nivelSelecionado.equals("Técnico")) {
            TelaCadastroTecnico IUCadTecnico = new TelaCadastroTecnico(this, true);
            IUCadTecnico.setLocationRelativeTo(this);
            IUCadTecnico.setVisible(true);
            int dep = IUCadTecnico.getDep();
            Funcionario func = IUCadTecnico.getFun();
            if(control.buscaFuncionarioCod(func.getCodigo()) == null){
              control.cadastrarFuncionario(func, dep);  
            } else JOptionPane.showMessageDialog(rootPane, "Funcionário já cadastrado com o código inserido");
        } else if (nivelSelecionado.equals("Efetivo")) {
            TelaCadastroEfetivo IUCadEfetivo = new TelaCadastroEfetivo(this, true);
            IUCadEfetivo.setLocationRelativeTo(this);
            IUCadEfetivo.setVisible(true);
            int dep = IUCadEfetivo.getDep();
            Funcionario func = IUCadEfetivo.getFun();
            if(control.buscaFuncionarioCod(func.getCodigo()) == null){
              control.cadastrarFuncionario(func, dep);  
            } else JOptionPane.showMessageDialog(rootPane, "Funcionário já cadastrado com o código inserido");
        } else {
            TelaCadastroSubstituto IUCadSubstituto = new TelaCadastroSubstituto(this, true);
            IUCadSubstituto.setLocationRelativeTo(this);
            IUCadSubstituto.setVisible(true);
            int dep = IUCadSubstituto.getDep();
            Funcionario func = IUCadSubstituto.getFun();
            if(control.buscaFuncionarioCod(func.getCodigo()) == null){
              control.cadastrarFuncionario(func, dep);  
            } else JOptionPane.showMessageDialog(rootPane, "Funcionário já cadastrado com o código inserido");
        }

    }//GEN-LAST:event_buttonCadastroActionPerformed

