    private void btnBuscarActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btnBuscarActionPerformed
        // TODO add your handling code here:
        String codNome = (String) comboCodNome.getSelectedItem();
        String s = "";
        String textCodNome = this.textCodNome.getText();
        if (codNome.equals("Código")) {
            s = control.buscaFuncionarioCod(textCodNome);
        } else if (codNome.equals("Nome")) {
            s = control.buscaFuncionarioNome(textCodNome);
        }
        if (s == null) {
            s = "Funcionário não encontrado!";
        }
        TelaRelatorio relatorio = new TelaRelatorio(this, true, s);
        relatorio.setLocationRelativeTo(this);
        relatorio.setVisible(true);

    }//GEN-LAST:event_btnBuscarActionPerformed

